import React, { useEffect } from 'react'
import { useDispatch, useSelector } from 'react-redux';
import Select from 'react-select'
import {changeCategory } from '../stores/categorySlice';
import { setSelectedValue } from '../stores/categorySlice';





const options = [

  { value: 'electronics', label: 'electronics' },
  { value: 'jewelery', label: 'jewelery' },
  { value: 'men\'s clothing', label: 'men\'s clothing' },
  { value: 'women\'s clothing', label: 'women\'s clothing' }
]



const Category = () => {
    
    const dispatch = useDispatch();

    const handleChange = (event) => {
      const selectedValue = event.target.value;
      dispatch(setSelectedValue(selectedValue));
    };

  return (

    <select id="categorySelect" onChange={handleChange}>
        <option value="">-- Select --</option>
        <option value="category1">electronics</option>
        <option value="category2">jewelery</option>
        <option value="category3">men's clothing</option>
        <option value="category3">women's clothing</option>
      </select>

  )
}

export default Category;
