import { configureStore, createSlice } from '@reduxjs/toolkit';

const selectSlice = createSlice({
  name: 'category',
  initialState: false,
  reducers: {
    changeCategory: (state) => !state,
  },
});

export const { toggleDarkMode } = selectSlice.actions;

const categorySlice = configureStore({
  reducer: {
    changeCategory: selectSlice.reducer,
  },
});

export default categorySlice;
