import React, { useEffect } from 'react'
import Select from 'react-select'
import { useDispatch, useSelector } from 'react-redux'
import { fetchProducts, STATUSES } from '../stores/productSlice';
import { add } from '../stores/cartSlice';





const options = [

  { value: 'chocolate', label: 'Chocolate' },
  { value: 'strawberry', label: 'Strawberry' },
  { value: 'vanilla', label: 'Vanilla' }
]

const Category = () => {

    const {data: categories} = useSelector((state) => state.category)
    const dispatch = useDispatch();

    useEffect(() => {
        dispatch(fetchProducts())
    }, [dispatch])

    const handleAdd = (product) => {
        dispatch(add(product))
    }

    if (categories.status === STATUSES.ERROR) return <h2>Something went wrong</h2>
    if (categories.status === STATUSES.LOADING) return <h2>Loading...</h2>



  return (
    <Select options={options} />
  )
}

export default Category;
